	public static final ThemeResource getAttachmentFileTypeIcon(String mimeType,String fileName){			
		if(mimeType.contains("image/bmp")||mimeType.contains("image/gif")||mimeType.contains("image/jpeg")||mimeType.contains("image/png")){
			return new ThemeResource("../vftheme/img/application/activityManage/fileType/jpg.png");
		}	
		else if(mimeType.contains("text/")){return new ThemeResource("../vftheme/img/application/activityManage/fileType/txt.png");}
		else if(mimeType.contains("application/pdf")){return new ThemeResource("../vftheme/img/application/activityManage/fileType/pdf.png");}
		else if(mimeType.contains("msword")){							
			if(mimeType.contains("msexcel")){
				return new ThemeResource("../vftheme/img/application/activityManage/fileType/xls.png");
			}else if(mimeType.contains("powerpoint")){
				return new ThemeResource("../vftheme/img/application/activityManage/fileType/ppt.png");
			}else{
				return new ThemeResource("../vftheme/img/application/activityManage/fileType/doc.png");
			}						
		}
		else if(mimeType.contains("officedocument.spreadsheetml.sheet")){
			return new ThemeResource("../vftheme/img/application/activityManage/fileType/xls.png");					
		}else if(mimeType.contains("officedocument.wordprocessingml.document")){
			return new ThemeResource("../vftheme/img/application/activityManage/fileType/doc.png");
		}else if(mimeType.contains("photoshop")){
			return new ThemeResource("../vftheme/img/application/activityManage/fileType/psd.png");
		}else if(mimeType.contains("mpeg3")){
			return new ThemeResource("../vftheme/img/application/activityManage/fileType/mp3.png");
		}
		else{
			if(fileName.endsWith(".pptx")){
				return new ThemeResource("../vftheme/img/application/activityManage/fileType/ppt.png");
			}		
			return new ThemeResource("../vftheme/img/application/activityManage/fileType/commonFile.png"); 
		}
	}
	
